mapper.delete:
        delete from game_product where product_id in

         #{productId}

--    删除主键，对应外键删除

mapper.select:
select * from game_product gp join product_rating_agg pra on gp.product_id = pra.product_id


    and product_name like concat('%',#{productName},'%')


    and category = #{category}


    and status = #{status}


    and avg_rating >= #{avgRating}


    and supplier_info like concat('%',#{supplierInfo},'%')


    and price <= #{price}
